import React, { useState } from 'react';
import { format } from 'date-fns';
import { 
  Eye, 
  Calendar, 
  AlertTriangle, 
  Pill, 
  Activity, 
  Thermometer,
  Clock,
  User,
  FileText
} from 'lucide-react';
import Modal from '../common/Modal';

const EntSection = ({ carePlans, onRefresh, isPatientView = false }) => {
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);

  const handleViewDetails = (plan) => {
    setSelectedPlan(plan);
    setShowDetailModal(true);
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'urgent': return 'priority-urgent';
      case 'high': return 'priority-high';
      case 'medium': return 'priority-medium';
      case 'low': return 'priority-low';
      default: return 'priority-medium';
    }
  };

  const getOperationTypeColor = (type) => {
    return type === 'pre-operation' ? 'badge-warning' : 'badge-info';
  };

  const getPainLevelColor = (level) => {
    if (level <= 3) return 'text-green-600';
    if (level <= 6) return 'text-yellow-600';
    return 'text-red-600';
  };

  if (carePlans.length === 0) {
    return (
      <div className="empty-state">
        <Activity className="empty-state-icon" />
        <h3 className="empty-state-title">No ENT Care Plans</h3>
        <p className="empty-state-description">
          {isPatientView 
            ? "You don't have any ENT care plans at the moment."
            : "No ENT care plans have been created yet."
          }
        </p>
      </div>
    );
  }

  return (
    <>
      {/* Card View */}
      <div className="care-plans-grid">
        {carePlans.map((plan) => (
          <div key={plan._id} className="care-plan-card">
            <div className={`priority-indicator ${getPriorityColor(plan.priority)}`}></div>

            <div className="card-header">
              <div>
                <h3 className="card-title">{plan.surgeryType}</h3>
                <p className="card-subtitle">
                  <User size={14} />
                  {plan.patientName} â€¢ Dr. {plan.doctorName}
                </p>
              </div>
              <span className={`badge ${getOperationTypeColor(plan.operationType)}`}>
                {plan.operationType}
              </span>
            </div>

            <div className="card-content">
              {/* Pain Level */}
              <div className="care-detail">
                <Thermometer size={16} />
                <span>Pain Level: </span>
                <span className={getPainLevelColor(plan.careDetails?.painLevel || 0)}>
                  {plan.careDetails?.painLevel || 0}/10
                </span>
              </div>

              {/* Breathing Issues */}
              {plan.careDetails?.breathingIssues && plan.careDetails.breathingIssues !== 'none' && (
                <div className="care-detail">
                  <Activity size={16} />
                  <span>Breathing: {plan.careDetails.breathingIssues}</span>
                </div>
              )}

              {/* Medications */}
              {plan.careDetails?.medicationIntake?.length > 0 && (
                <div className="care-detail">
                  <Pill size={16} />
                  <span>{plan.careDetails.medicationIntake.length} medication(s)</span>
                </div>
              )}

              {/* Healing Progress */}
              {plan.careDetails?.healingProgress?.woundCondition && (
                <div className="care-detail">
                  <Activity size={16} />
                  <span>Healing: {plan.careDetails.healingProgress.woundCondition}</span>
                </div>
              )}

              {/* Instructions Preview */}
              <div className="instructions-preview">
                <FileText size={14} />
                <span>{plan.instructions.substring(0, 100)}...</span>
              </div>
            </div>

            <div className="card-meta">
              <span>
                <Calendar size={12} />
                {format(new Date(plan.createdAt), 'MMM dd, yyyy')}
              </span>
              <span className={`badge badge-${plan.status === 'active' ? 'success' : 'info'}`}>
                {plan.status}
              </span>
            </div>

            <div className="card-actions">
              <button
                onClick={() => handleViewDetails(plan)}
                className="btn btn-outline"
              >
                <Eye size={16} />
                View Details
              </button>
            </div>
          </div>
        ))}
      </div>


      {/* Detail Modal */}
      <Modal
        isOpen={showDetailModal}
        onClose={() => setShowDetailModal(false)}
        title="ENT Care Plan Details"
        size="large"
      >
        {selectedPlan && (
          <div className="care-plan-details">
            {/* Basic Information */}
            <div className="detail-section">
              <h3>Basic Information</h3>
              <div className="detail-grid">
                <div className="detail-item">
                  <label>Patient Name:</label>
                  <span>{selectedPlan.patientName}</span>
                </div>
                <div className="detail-item">
                  <label>Doctor:</label>
                  <span>Dr. {selectedPlan.doctorName}</span>
                </div>
                <div className="detail-item">
                  <label>Surgery Type:</label>
                  <span>{selectedPlan.surgeryType}</span>
                </div>
                <div className="detail-item">
                  <label>Operation Type:</label>
                  <span className={`badge ${getOperationTypeColor(selectedPlan.operationType)}`}>
                    {selectedPlan.operationType}
                  </span>
                </div>
                <div className="detail-item">
                  <label>Priority:</label>
                  <span className={`badge badge-${selectedPlan.priority === 'high' ? 'error' : selectedPlan.priority === 'medium' ? 'warning' : 'success'}`}>
                    {selectedPlan.priority}
                  </span>
                </div>
                <div className="detail-item">
                  <label>Status:</label>
                  <span className={`badge badge-${selectedPlan.status === 'active' ? 'success' : 'info'}`}>
                    {selectedPlan.status}
                  </span>
                </div>
              </div>
            </div>

            {/* Care Details */}
            <div className="detail-section">
              <h3>Care Details</h3>
              <div className="detail-grid">
                <div className="detail-item">
                  <label>Pain Level:</label>
                  <span className={getPainLevelColor(selectedPlan.careDetails?.painLevel || 0)}>
                    {selectedPlan.careDetails?.painLevel || 0}/10
                  </span>
                </div>
                <div className="detail-item">
                  <label>Breathing Issues:</label>
                  <span>{selectedPlan.careDetails?.breathingIssues || 'None'}</span>
                </div>
                <div className="detail-item">
                  <label>Throat Discomfort:</label>
                  <span>{selectedPlan.careDetails?.throatDiscomfort || 'None'}</span>
                </div>
              </div>
            </div>

            {/* Medications */}
            {selectedPlan.careDetails?.medicationIntake?.length > 0 && (
              <div className="detail-section">
                <h3>Medications</h3>
                <div className="medications-list">
                  {selectedPlan.careDetails.medicationIntake.map((med, index) => (
                    <div key={index} className="medication-item">
                      <div className="medication-name">{med.medicationName}</div>
                      <div className="medication-details">
                        <span>Dosage: {med.dosage}</span>
                        <span>Frequency: {med.frequency}</span>
                        {med.sideEffects && med.sideEffects !== 'none' && (
                          <span>Side Effects: {med.sideEffects}</span>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Healing Progress */}
            {selectedPlan.careDetails?.healingProgress && (
              <div className="detail-section">
                <h3>Healing Progress</h3>
                <div className="detail-grid">
                  <div className="detail-item">
                    <label>Wound Condition:</label>
                    <span>{selectedPlan.careDetails.healingProgress.woundCondition}</span>
                  </div>
                  <div className="detail-item">
                    <label>Swelling:</label>
                    <span>{selectedPlan.careDetails.healingProgress.swelling}</span>
                  </div>
                  {selectedPlan.careDetails.healingProgress.notes && (
                    <div className="detail-item full-width">
                      <label>Notes:</label>
                      <span>{selectedPlan.careDetails.healingProgress.notes}</span>
                    </div>
                  )}
                </div>
              </div>
            )}

            {/* Instructions */}
            <div className="detail-section">
              <h3>Instructions</h3>
              <div className="instructions-content">
                {selectedPlan.instructions}
              </div>
            </div>

            {/* Symptoms */}
            {selectedPlan.symptoms && (
              <div className="detail-section">
                <h3>Symptoms</h3>
                <div className="symptoms-content">
                  {selectedPlan.symptoms}
                </div>
              </div>
            )}

            {/* Timestamps */}
            <div className="detail-section">
              <h3>Timeline</h3>
              <div className="detail-grid">
                <div className="detail-item">
                  <label>Created:</label>
                  <span>{format(new Date(selectedPlan.createdAt), 'PPpp')}</span>
                </div>
                <div className="detail-item">
                  <label>Last Updated:</label>
                  <span>{format(new Date(selectedPlan.updatedAt), 'PPpp')}</span>
                </div>
                {selectedPlan.nextAppointment && (
                  <div className="detail-item">
                    <label>Next Appointment:</label>
                    <span>{format(new Date(selectedPlan.nextAppointment), 'PPpp')}</span>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </Modal>
    </>
  );
};

export default EntSection;
