import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { entService } from '../../services/entService';
import { obstetricsService } from '../../services/obstetricsService';
import toast from 'react-hot-toast';
import { 
  Stethoscope, 
  Baby, 
  Plus, 
  Users, 
  Calendar, 
  TrendingUp,
  Shield,
  Search,
  Filter
} from 'lucide-react';
import LoadingSpinner from '../common/LoadingSpinner';
import AdminEntSection from './AdminEntSection';
import AdminObstetricsSection from './AdminObstetricsSection';
import CreateCarePlanModal from './CreateCarePlanModal';

const AdminDashboard = () => {
  const { user } = useAuth();
  const [entCarePlans, setEntCarePlans] = useState([]);
  const [obstetricsCarePlans, setObstetricsCarePlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeSection, setActiveSection] = useState('both');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [createModalType, setCreateModalType] = useState('ent');
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [filterPriority, setFilterPriority] = useState('all');

  useEffect(() => {
    fetchAllCarePlans();
  }, []);

  const fetchAllCarePlans = async () => {
    try {
      setLoading(true);

      // Fetch all ENT care plans
      const entResponse = await entService.getAllCarePlans({
        page: 1,
        limit: 100,
        sortBy: 'createdAt',
        sortOrder: 'desc'
      });
      if (entResponse.success) {
        setEntCarePlans(entResponse.data);
      }

      // Fetch all Obstetrics care plans
      const obsResponse = await obstetricsService.getAllCarePlans({
        page: 1,
        limit: 100,
        sortBy: 'createdAt',
        sortOrder: 'desc'
      });
      if (obsResponse.success) {
        setObstetricsCarePlans(obsResponse.data);
      }
    } catch (error) {
      toast.error('Failed to load care plans');
      console.error('Error fetching care plans:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateCarePlan = (type) => {
    setCreateModalType(type);
    setShowCreateModal(true);
  };

  const handleCarePlanCreated = () => {
    fetchAllCarePlans();
    setShowCreateModal(false);
    toast.success('Care plan created successfully');
  };

  const handleRefresh = () => {
    fetchAllCarePlans();
    toast.success('Care plans refreshed');
  };

  // Filter care plans based on search and filters
  const filterCarePlans = (plans) => {
    return plans.filter(plan => {
      const matchesSearch = !searchTerm || 
        plan.patientName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        plan.doctorName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (plan.surgeryType && plan.surgeryType.toLowerCase().includes(searchTerm.toLowerCase()));

      const matchesStatus = filterStatus === 'all' || plan.status === filterStatus;
      const matchesPriority = filterPriority === 'all' || plan.priority === filterPriority;

      return matchesSearch && matchesStatus && matchesPriority;
    });
  };

  const filteredEntPlans = filterCarePlans(entCarePlans);
  const filteredObsPlans = filterCarePlans(obstetricsCarePlans);

  // Calculate statistics
  const totalPlans = entCarePlans.length + obstetricsCarePlans.length;
  const activePlans = entCarePlans.filter(p => p.status === 'active').length + 
                     obstetricsCarePlans.filter(p => p.status === 'active').length;
  const urgentPlans = entCarePlans.filter(p => p.priority === 'urgent').length + 
                      obstetricsCarePlans.filter(p => p.priority === 'urgent').length;
  const uniquePatients = new Set([
    ...entCarePlans.map(p => p.patientId),
    ...obstetricsCarePlans.map(p => p.patientId)
  ]).size;

  if (loading) {
    return <LoadingSpinner size="large" text="Loading admin dashboard..." />;
  }

  return (
    <div className="dashboard-container">
      {/* Admin Dashboard Header */}
      <div className="dashboard-header">
        <div className="admin-header-content">
          <div className="admin-title-section">
            <div className="admin-badge">
              <Shield size={20} />
              <span>Doctor Portal</span>
            </div>
            <h1 className="dashboard-title">Care Plan Management</h1>
            <p className="dashboard-subtitle">
              Welcome, Dr. {user.name}! Manage patient care plans and monitor recovery progress.
            </p>
          </div>

          <div className="admin-actions">
            <button
              onClick={() => handleCreateCarePlan('ent')}
              className="btn btn-primary"
            >
              <Plus size={16} />
              New ENT Plan
            </button>
            <button
              onClick={() => handleCreateCarePlan('obstetrics')}
              className="btn btn-secondary"
            >
              <Plus size={16} />
              New Obstetrics Plan
            </button>
          </div>
        </div>

        {/* Statistics Dashboard */}
        <div className="admin-stats">
          <div className="stat-card">
            <div className="stat-icon total-icon">
              <Calendar size={24} />
            </div>
            <div className="stat-content">
              <div className="stat-number">{totalPlans}</div>
              <div className="stat-label">Total Care Plans</div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon active-icon">
              <TrendingUp size={24} />
            </div>
            <div className="stat-content">
              <div className="stat-number">{activePlans}</div>
              <div className="stat-label">Active Plans</div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon urgent-icon">
              <Shield size={24} />
            </div>
            <div className="stat-content">
              <div className="stat-number">{urgentPlans}</div>
              <div className="stat-label">Urgent Cases</div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon patients-icon">
              <Users size={24} />
            </div>
            <div className="stat-content">
              <div className="stat-number">{uniquePatients}</div>
              <div className="stat-label">Unique Patients</div>
            </div>
          </div>
        </div>

        {/* Search and Filters */}
        <div className="admin-controls">
          <div className="search-section">
            <div className="search-input-container">
              <Search size={16} />
              <input
                type="text"
                placeholder="Search patients, doctors, or procedures..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
            </div>
          </div>

          <div className="filter-section">
            <div className="filter-group">
              <Filter size={16} />
              <select
                value={filterStatus}
                onChange={(e) => setFilterStatus(e.target.value)}
                className="filter-select"
              >
                <option value="all">All Status</option>
                <option value="active">Active</option>
                <option value="completed">Completed</option>
                <option value="cancelled">Cancelled</option>
              </select>
            </div>

            <div className="filter-group">
              <select
                value={filterPriority}
                onChange={(e) => setFilterPriority(e.target.value)}
                className="filter-select"
              >
                <option value="all">All Priority</option>
                <option value="urgent">Urgent</option>
                <option value="high">High</option>
                <option value="medium">Medium</option>
                <option value="low">Low</option>
              </select>
            </div>
          </div>
        </div>

        {/* Section Toggle */}
        <div className="section-toggle">
          <button
            className={`toggle-btn ${activeSection === 'both' ? 'active' : ''}`}
            onClick={() => setActiveSection('both')}
          >
            All Care Plans
          </button>
          <button
            className={`toggle-btn ${activeSection === 'ent' ? 'active' : ''}`}
            onClick={() => setActiveSection('ent')}
          >
            <Stethoscope size={16} />
            ENT ({filteredEntPlans.length})
          </button>
          <button
            className={`toggle-btn ${activeSection === 'obstetrics' ? 'active' : ''}`}
            onClick={() => setActiveSection('obstetrics')}
          >
            <Baby size={16} />
            Obstetrics ({filteredObsPlans.length})
          </button>
        </div>

        {/* Refresh Button */}
        <button onClick={handleRefresh} className="btn btn-outline refresh-btn">
          <Calendar size={16} />
          Refresh Data
        </button>
      </div>

      {/* Care Sections - Stacked Vertically */}
      <div className="care-sections">
        {/* ENT Section */}
        {(activeSection === 'both' || activeSection === 'ent') && (
          <div className="care-section">
            <div className="section-header">
              <div className="section-title">
                <div className="section-icon ent-icon">
                  <Stethoscope size={24} />
                </div>
                ENT Care Plans
                <span className="section-count">({filteredEntPlans.length})</span>
              </div>
              <button
                onClick={() => handleCreateCarePlan('ent')}
                className="btn btn-primary"
              >
                <Plus size={16} />
                Add ENT Plan
              </button>
            </div>

            <AdminEntSection 
              carePlans={filteredEntPlans}
              onRefresh={fetchAllCarePlans}
              isAdminView={true}
            />
          </div>
        )}

        {/* Obstetrics Section */}
        {(activeSection === 'both' || activeSection === 'obstetrics') && (
          <div className="care-section">
            <div className="section-header">
              <div className="section-title">
                <div className="section-icon obstetrics-icon">
                  <Baby size={24} />
                </div>
                Obstetrics Care Plans
                <span className="section-count">({filteredObsPlans.length})</span>
              </div>
              <button
                onClick={() => handleCreateCarePlan('obstetrics')}
                className="btn btn-secondary"
              >
                <Plus size={16} />
                Add Obstetrics Plan
              </button>
            </div>

            <AdminObstetricsSection 
              carePlans={filteredObsPlans}
              onRefresh={fetchAllCarePlans}
              isAdminView={true}
            />
          </div>
        )}

        {/* Empty State */}
        {totalPlans === 0 && (
          <div className="empty-state">
            <Shield className="empty-state-icon" />
            <h3 className="empty-state-title">No Care Plans Created</h3>
            <p className="empty-state-description">
              Start by creating care plans for your patients using the buttons above.
            </p>
            <div className="empty-state-actions">
              <button
                onClick={() => handleCreateCarePlan('ent')}
                className="btn btn-primary"
              >
                <Plus size={16} />
                Create ENT Plan
              </button>
              <button
                onClick={() => handleCreateCarePlan('obstetrics')}
                className="btn btn-secondary"
              >
                <Plus size={16} />
                Create Obstetrics Plan
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Create Care Plan Modal */}
      <CreateCarePlanModal
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        onSuccess={handleCarePlanCreated}
        type={createModalType}
      />
    </div>
  );
};

export default AdminDashboard;
