import React, { useState } from 'react';
import { format } from 'date-fns';
import { 
  Eye, 
  Edit, 
  Trash2, 
  Calendar, 
  AlertTriangle, 
  Pill, 
  Activity, 
  Thermometer,
  User,
  FileText,
  MoreVertical
} from 'lucide-react';
import { entService } from '../../services/entService';
import toast from 'react-hot-toast';
import Modal from '../common/Modal';
import EditEntCarePlanModal from './EditEntCarePlanModal';

const AdminEntSection = ({ carePlans, onRefresh, isAdminView = false }) => {
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleViewDetails = (plan) => {
    setSelectedPlan(plan);
    setShowDetailModal(true);
  };

  const handleEdit = (plan) => {
    setSelectedPlan(plan);
    setShowEditModal(true);
  };

  const handleDelete = (plan) => {
    setSelectedPlan(plan);
    setShowDeleteConfirm(true);
  };

  const confirmDelete = async () => {
    if (!selectedPlan) return;

    setLoading(true);
    try {
      const result = await entService.deleteCarePlan(selectedPlan._id);
      if (result.success) {
        toast.success('ENT care plan deleted successfully');
        onRefresh();
      } else {
        toast.error(result.message || 'Failed to delete care plan');
      }
    } catch (error) {
      toast.error('Error deleting care plan');
    } finally {
      setLoading(false);
      setShowDeleteConfirm(false);
      setSelectedPlan(null);
    }
  };

  const handleEditSuccess = () => {
    setShowEditModal(false);
    setSelectedPlan(null);
    onRefresh();
    toast.success('ENT care plan updated successfully');
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'urgent': return 'priority-urgent';
      case 'high': return 'priority-high';
      case 'medium': return 'priority-medium';
      case 'low': return 'priority-low';
      default: return 'priority-medium';
    }
  };

  const getOperationTypeColor = (type) => {
    return type === 'pre-operation' ? 'badge-warning' : 'badge-info';
  };

  const getPainLevelColor = (level) => {
    if (level <= 3) return 'text-green-600';
    if (level <= 6) return 'text-yellow-600';
    return 'text-red-600';
  };

  if (carePlans.length === 0) {
    return (
      <div className="empty-state">
        <Activity className="empty-state-icon" />
        <h3 className="empty-state-title">No ENT Care Plans</h3>
        <p className="empty-state-description">
          No ENT care plans match your current filters.
        </p>
      </div>
    );
  }

  return (
    <>
      {/* Card View */}
      <div className="care-plans-grid">
        {carePlans.map((plan) => (
          <div key={plan._id} className="care-plan-card admin-card">
            <div className={`priority-indicator ${getPriorityColor(plan.priority)}`}></div>

            <div className="card-header">
              <div>
                <h3 className="card-title">{plan.surgeryType}</h3>
                <p className="card-subtitle">
                  <User size={14} />
                  {plan.patientName} • Dr. {plan.doctorName}
                </p>
              </div>
              <div className="card-header-actions">
                <span className={`badge ${getOperationTypeColor(plan.operationType)}`}>
                  {plan.operationType}
                </span>
                <div className="dropdown">
                  <button className="dropdown-trigger">
                    <MoreVertical size={16} />
                  </button>
                  <div className="dropdown-menu">
                    <button onClick={() => handleViewDetails(plan)}>
                      <Eye size={14} />
                      View Details
                    </button>
                    <button onClick={() => handleEdit(plan)}>
                      <Edit size={14} />
                      Edit Plan
                    </button>
                    <button onClick={() => handleDelete(plan)} className="danger">
                      <Trash2 size={14} />
                      Delete Plan
                    </button>
                  </div>
                </div>
              </div>
            </div>

            <div className="card-content">
              {/* Pain Level */}
              <div className="care-detail">
                <Thermometer size={16} />
                <span>Pain Level: </span>
                <span className={getPainLevelColor(plan.careDetails?.painLevel || 0)}>
                  {plan.careDetails?.painLevel || 0}/10
                </span>
              </div>

              {/* Breathing Issues */}
              {plan.careDetails?.breathingIssues && plan.careDetails.breathingIssues !== 'none' && (
                <div className="care-detail">
                  <Activity size={16} />
                  <span>Breathing: {plan.careDetails.breathingIssues}</span>
                </div>
              )}

              {/* Medications */}
              {plan.careDetails?.medicationIntake?.length > 0 && (
                <div className="care-detail">
                  <Pill size={16} />
                  <span>{plan.careDetails.medicationIntake.length} medication(s)</span>
                </div>
              )}

              {/* Healing Progress */}
              {plan.careDetails?.healingProgress?.woundCondition && (
                <div className="care-detail">
                  <Activity size={16} />
                  <span>Healing: {plan.careDetails.healingProgress.woundCondition}</span>
                </div>
              )}

              {/* Instructions Preview */}
              <div className="instructions-preview">
                <FileText size={14} />
                <span>{plan.instructions.substring(0, 80)}...</span>
              </div>
            </div>

            <div className="card-meta">
              <span>
                <Calendar size={12} />
                {format(new Date(plan.createdAt), 'MMM dd, yyyy')}
              </span>
              <span className={`badge badge-${plan.status === 'active' ? 'success' : 'info'}`}>
                {plan.status}
              </span>
            </div>

            {isAdminView && (
              <div className="card-actions">
                <button
                  onClick={() => handleViewDetails(plan)}
                  className="btn btn-outline btn-sm"
                >
                  <Eye size={14} />
                  View
                </button>
                <button
                  onClick={() => handleEdit(plan)}
                  className="btn btn-primary btn-sm"
                >
                  <Edit size={14} />
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(plan)}
                  className="btn btn-danger btn-sm"
                >
                  <Trash2 size={14} />
                  Delete
                </button>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Detailed Table View */}
      <div className="table-container">
        <table className="table">
          <thead>
            <tr>
              <th>Patient</th>
              <th>Surgery Type</th>
              <th>Operation Type</th>
              <th>Pain Level</th>
              <th>Status</th>
              <th>Priority</th>
              <th>Created</th>
              {isAdminView && <th>Actions</th>}
            </tr>
          </thead>
          <tbody>
            {carePlans.map((plan) => (
              <tr key={plan._id}>
                <td>
                  <div>
                    <div className="font-medium">{plan.patientName}</div>
                    <div className="text-sm text-gray-500">ID: {plan.patientId}</div>
                    <div className="text-sm text-gray-500">Dr. {plan.doctorName}</div>
                  </div>
                </td>
                <td>{plan.surgeryType}</td>
                <td>
                  <span className={`badge ${getOperationTypeColor(plan.operationType)}`}>
                    {plan.operationType}
                  </span>
                </td>
                <td>
                  <span className={getPainLevelColor(plan.careDetails?.painLevel || 0)}>
                    {plan.careDetails?.painLevel || 0}/10
                  </span>
                </td>
                <td>
                  <span className={`badge badge-${plan.status === 'active' ? 'success' : 'info'}`}>
                    {plan.status}
                  </span>
                </td>
                <td>
                  <span className={`badge badge-${plan.priority === 'urgent' ? 'error' : plan.priority === 'high' ? 'error' : plan.priority === 'medium' ? 'warning' : 'success'}`}>
                    {plan.priority}
                  </span>
                </td>
                <td>{format(new Date(plan.createdAt), 'MMM dd, yyyy')}</td>
                {isAdminView && (
                  <td>
                    <div className="table-actions">
                      <button
                        onClick={() => handleViewDetails(plan)}
                        className="btn btn-outline btn-sm"
                        title="View Details"
                      >
                        <Eye size={14} />
                      </button>
                      <button
                        onClick={() => handleEdit(plan)}
                        className="btn btn-primary btn-sm"
                        title="Edit Plan"
                      >
                        <Edit size={14} />
                      </button>
                      <button
                        onClick={() => handleDelete(plan)}
                        className="btn btn-danger btn-sm"
                        title="Delete Plan"
                      >
                        <Trash2 size={14} />
                      </button>
                    </div>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Detail Modal */}
      <Modal
        isOpen={showDetailModal}
        onClose={() => setShowDetailModal(false)}
        title="ENT Care Plan Details"
        size="large"
      >
        {selectedPlan && (
          <div className="care-plan-details">
            {/* Same detail content as patient view but with admin context */}
            <div className="detail-section">
              <h3>Basic Information</h3>
              <div className="detail-grid">
                <div className="detail-item">
                  <label>Patient ID:</label>
                  <span>{selectedPlan.patientId}</span>
                </div>
                <div className="detail-item">
                  <label>Patient Name:</label>
                  <span>{selectedPlan.patientName}</span>
                </div>
                <div className="detail-item">
                  <label>Doctor ID:</label>
                  <span>{selectedPlan.doctorId}</span>
                </div>
                <div className="detail-item">
                  <label>Doctor:</label>
                  <span>Dr. {selectedPlan.doctorName}</span>
                </div>
                <div className="detail-item">
                  <label>Surgery Type:</label>
                  <span>{selectedPlan.surgeryType}</span>
                </div>
                <div className="detail-item">
                  <label>Operation Type:</label>
                  <span className={`badge ${getOperationTypeColor(selectedPlan.operationType)}`}>
                    {selectedPlan.operationType}
                  </span>
                </div>
              </div>
            </div>

            {/* Admin Actions in Modal */}
            <div className="modal-admin-actions">
              <button
                onClick={() => {
                  setShowDetailModal(false);
                  handleEdit(selectedPlan);
                }}
                className="btn btn-primary"
              >
                <Edit size={16} />
                Edit Care Plan
              </button>
              <button
                onClick={() => {
                  setShowDetailModal(false);
                  handleDelete(selectedPlan);
                }}
                className="btn btn-danger"
              >
                <Trash2 size={16} />
                Delete Care Plan
              </button>
            </div>
          </div>
        )}
      </Modal>

      {/* Edit Modal */}
      <EditEntCarePlanModal
        isOpen={showEditModal}
        onClose={() => setShowEditModal(false)}
        onSuccess={handleEditSuccess}
        carePlan={selectedPlan}
      />

      {/* Delete Confirmation Modal */}
      <Modal
        isOpen={showDeleteConfirm}
        onClose={() => setShowDeleteConfirm(false)}
        title="Confirm Deletion"
        size="small"
      >
        <div className="delete-confirmation">
          <div className="delete-icon">
            <AlertTriangle size={48} className="text-red-500" />
          </div>
          <h3>Delete Care Plan?</h3>
          <p>
            Are you sure you want to delete the care plan for <strong>{selectedPlan?.patientName}</strong>?
            This action cannot be undone.
          </p>
          <div className="delete-actions">
            <button
              onClick={() => setShowDeleteConfirm(false)}
              className="btn btn-outline"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              onClick={confirmDelete}
              className="btn btn-danger"
              disabled={loading}
            >
              {loading ? 'Deleting...' : 'Delete Care Plan'}
            </button>
          </div>
        </div>
      </Modal>
    </>
  );
};

export default AdminEntSection;
