import React, { useState } from 'react';
import { useForm, useFieldArray } from 'react-hook-form';
import { entService } from '../../services/entService';
import { obstetricsService } from '../../services/obstetricsService';
import toast from 'react-hot-toast';
import { Plus, Minus, Save, X } from 'lucide-react';
import Modal from '../common/Modal';
import LoadingSpinner from '../common/LoadingSpinner';

const CreateCarePlanModal = ({ isOpen, onClose, onSuccess, type }) => {
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('basic');

  const {
    register,
    handleSubmit,
    control,
    reset,
    formState: { errors },
    watch
  } = useForm({
    defaultValues: {
      patientId: '',
      patientName: '',
      doctorId: '',
      doctorName: '',
      instructions: '',
      priority: 'medium',
      status: 'active',
      // ENT specific
      operationType: 'post-operation',
      surgeryType: '',
      careDetails: {
        painLevel: 0,
        breathingIssues: 'none',
        throatDiscomfort: 'none',
        medicationIntake: [],
        healingProgress: {
          woundCondition: 'good',
          swelling: 'none',
          notes: ''
        }
      },
      // Obstetrics specific
      careType: 'prenatal',
      gestationalWeek: '',
      careDetails: {
        trimesterSymptoms: {
          nausea: 'none',
          cramps: 'none',
          mood: 'stable',
          fatigue: 'none'
        },
        babyMovement: {
          frequency: 'normal',
          notes: ''
        },
        sleepNutrition: {
          sleepHours: 8,
          sleepQuality: 'good',
          dietNotes: '',
          waterIntake: 2,
          supplements: []
        },
        postnatalRecovery: {
          bleeding: 'none',
          stitchesCondition: 'not-applicable',
          painLevel: 0,
          emotionalState: 'stable',
          breastfeedingLogs: []
        }
      },
      vitalSigns: {
        bloodPressure: {
          systolic: '',
          diastolic: ''
        },
        weight: '',
        temperature: '',
        heartRate: ''
      }
    }
  });

  const { fields: medicationFields, append: appendMedication, remove: removeMedication } = useFieldArray({
    control,
    name: 'careDetails.medicationIntake'
  });

  const { fields: supplementFields, append: appendSupplement, remove: removeSupplement } = useFieldArray({
    control,
    name: 'careDetails.sleepNutrition.supplements'
  });

  const onSubmit = async (data) => {
    setLoading(true);
    try {
      let result;

      if (type === 'ent') {
        result = await entService.createCarePlan(data);
      } else {
        result = await obstetricsService.createCarePlan(data);
      }

      if (result.success) {
        toast.success(`${type === 'ent' ? 'ENT' : 'Obstetrics'} care plan created successfully`);
        reset();
        onSuccess();
      } else {
        toast.error(result.message || 'Failed to create care plan');
        if (result.errors) {
          console.error('Validation errors:', result.errors);
        }
      }
    } catch (error) {
      toast.error('Error creating care plan');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    reset();
    onClose();
  };

  const tabs = [
    { id: 'basic', label: 'Basic Info' },
    { id: 'care', label: 'Care Details' },
    { id: 'vitals', label: type === 'obstetrics' ? 'Vital Signs' : 'Additional' }
  ];

  return (
    <Modal
      isOpen={isOpen}
      onClose={handleClose}
      title={`Create ${type === 'ent' ? 'ENT' : 'Obstetrics'} Care Plan`}
      size="large"
    >
      <form onSubmit={handleSubmit(onSubmit)} className="create-care-plan-form">
        {/* Tab Navigation */}
        <div className="tab-navigation">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              type="button"
              className={`tab-btn ${activeTab === tab.id ? 'active' : ''}`}
              onClick={() => setActiveTab(tab.id)}
            >
              {tab.label}
            </button>
          ))}
        </div>

        {/* Basic Information Tab */}
        {activeTab === 'basic' && (
          <div className="tab-content">
            <div className="form-grid">
              <div className="form-group">
                <label className="form-label">Patient ID *</label>
                <input
                  type="text"
                  className={`form-input ${errors.patientId ? 'error' : ''}`}
                  {...register('patientId', { required: 'Patient ID is required' })}
                />
                {errors.patientId && (
                  <span className="error-message">{errors.patientId.message}</span>
                )}
              </div>

              <div className="form-group">
                <label className="form-label">Patient Name *</label>
                <input
                  type="text"
                  className={`form-input ${errors.patientName ? 'error' : ''}`}
                  {...register('patientName', { required: 'Patient name is required' })}
                />
                {errors.patientName && (
                  <span className="error-message">{errors.patientName.message}</span>
                )}
              </div>

              <div className="form-group">
                <label className="form-label">Doctor ID *</label>
                <input
                  type="text"
                  className={`form-input ${errors.doctorId ? 'error' : ''}`}
                  {...register('doctorId', { required: 'Doctor ID is required' })}
                />
                {errors.doctorId && (
                  <span className="error-message">{errors.doctorId.message}</span>
                )}
              </div>

              <div className="form-group">
                <label className="form-label">Doctor Name *</label>
                <input
                  type="text"
                  className={`form-input ${errors.doctorName ? 'error' : ''}`}
                  {...register('doctorName', { required: 'Doctor name is required' })}
                />
                {errors.doctorName && (
                  <span className="error-message">{errors.doctorName.message}</span>
                )}
              </div>

              {type === 'ent' && (
                <>
                  <div className="form-group">
                    <label className="form-label">Operation Type *</label>
                    <select
                      className="form-select"
                      {...register('operationType', { required: 'Operation type is required' })}
                    >
                      <option value="pre-operation">Pre-operation</option>
                      <option value="post-operation">Post-operation</option>
                    </select>
                  </div>

                  <div className="form-group">
                    <label className="form-label">Surgery Type *</label>
                    <input
                      type="text"
                      className={`form-input ${errors.surgeryType ? 'error' : ''}`}
                      {...register('surgeryType', { required: 'Surgery type is required' })}
                    />
                    {errors.surgeryType && (
                      <span className="error-message">{errors.surgeryType.message}</span>
                    )}
                  </div>
                </>
              )}

              {type === 'obstetrics' && (
                <>
                  <div className="form-group">
                    <label className="form-label">Care Type *</label>
                    <select
                      className="form-select"
                      {...register('careType', { required: 'Care type is required' })}
                    >
                      <option value="prenatal">Prenatal</option>
                      <option value="postnatal">Postnatal</option>
                      <option value="delivery-prep">Delivery Preparation</option>
                    </select>
                  </div>

                  <div className="form-group">
                    <label className="form-label">Gestational Week</label>
                    <input
                      type="number"
                      min="0"
                      max="42"
                      className="form-input"
                      {...register('gestationalWeek', {
                        min: { value: 0, message: 'Must be at least 0' },
                        max: { value: 42, message: 'Must be at most 42' }
                      })}
                    />
                  </div>
                </>
              )}

              <div className="form-group">
                <label className="form-label">Priority</label>
                <select className="form-select" {...register('priority')}>
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                  <option value="urgent">Urgent</option>
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">Status</label>
                <select className="form-select" {...register('status')}>
                  <option value="active">Active</option>
                  <option value="completed">Completed</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>
            </div>

            <div className="form-group">
              <label className="form-label">Instructions *</label>
              <textarea
                className={`form-textarea ${errors.instructions ? 'error' : ''}`}
                rows="4"
                {...register('instructions', { required: 'Instructions are required' })}
              />
              {errors.instructions && (
                <span className="error-message">{errors.instructions.message}</span>
              )}
            </div>
          </div>
        )}

        {/* Care Details Tab */}
        {activeTab === 'care' && (
          <div className="tab-content">
            {type === 'ent' && (
              <>
                <div className="form-section">
                  <h4>Pain & Symptoms</h4>
                  <div className="form-grid">
                    <div className="form-group">
                      <label className="form-label">Pain Level (0-10)</label>
                      <input
                        type="number"
                        min="0"
                        max="10"
                        className="form-input"
                        {...register('careDetails.painLevel')}
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label">Breathing Issues</label>
                      <select className="form-select" {...register('careDetails.breathingIssues')}>
                        <option value="none">None</option>
                        <option value="mild">Mild</option>
                        <option value="moderate">Moderate</option>
                        <option value="severe">Severe</option>
                      </select>
                    </div>

                    <div className="form-group">
                      <label className="form-label">Throat Discomfort</label>
                      <select className="form-select" {...register('careDetails.throatDiscomfort')}>
                        <option value="none">None</option>
                        <option value="mild">Mild</option>
                        <option value="moderate">Moderate</option>
                        <option value="severe">Severe</option>
                      </select>
                    </div>
                  </div>
                </div>

                <div className="form-section">
                  <h4>Medications</h4>
                  {medicationFields.map((field, index) => (
                    <div key={field.id} className="medication-item">
                      <div className="form-grid">
                        <div className="form-group">
                          <label className="form-label">Medication Name</label>
                          <input
                            type="text"
                            className="form-input"
                            {...register(`careDetails.medicationIntake.${index}.medicationName`)}
                          />
                        </div>
                        <div className="form-group">
                          <label className="form-label">Dosage</label>
                          <input
                            type="text"
                            className="form-input"
                            {...register(`careDetails.medicationIntake.${index}.dosage`)}
                          />
                        </div>
                        <div className="form-group">
                          <label className="form-label">Frequency</label>
                          <input
                            type="text"
                            className="form-input"
                            {...register(`careDetails.medicationIntake.${index}.frequency`)}
                          />
                        </div>
                        <div className="form-group">
                          <label className="form-label">Side Effects</label>
                          <input
                            type="text"
                            className="form-input"
                            {...register(`careDetails.medicationIntake.${index}.sideEffects`)}
                          />
                        </div>
                      </div>
                      <button
                        type="button"
                        onClick={() => removeMedication(index)}
                        className="btn btn-danger btn-sm"
                      >
                        <Minus size={14} />
                        Remove
                      </button>
                    </div>
                  ))}
                  <button
                    type="button"
                    onClick={() => appendMedication({ medicationName: '', dosage: '', frequency: '', sideEffects: '' })}
                    className="btn btn-outline"
                  >
                    <Plus size={16} />
                    Add Medication
                  </button>
                </div>

                <div className="form-section">
                  <h4>Healing Progress</h4>
                  <div className="form-grid">
                    <div className="form-group">
                      <label className="form-label">Wound Condition</label>
                      <select className="form-select" {...register('careDetails.healingProgress.woundCondition')}>
                        <option value="excellent">Excellent</option>
                        <option value="good">Good</option>
                        <option value="fair">Fair</option>
                        <option value="poor">Poor</option>
                      </select>
                    </div>

                    <div className="form-group">
                      <label className="form-label">Swelling</label>
                      <select className="form-select" {...register('careDetails.healingProgress.swelling')}>
                        <option value="none">None</option>
                        <option value="mild">Mild</option>
                        <option value="moderate">Moderate</option>
                        <option value="severe">Severe</option>
                      </select>
                    </div>
                  </div>

                  <div className="form-group">
                    <label className="form-label">Healing Notes</label>
                    <textarea
                      className="form-textarea"
                      rows="3"
                      {...register('careDetails.healingProgress.notes')}
                    />
                  </div>
                </div>
              </>
            )}

            {type === 'obstetrics' && (
              <>
                <div className="form-section">
                  <h4>Trimester Symptoms</h4>
                  <div className="form-grid">
                    <div className="form-group">
                      <label className="form-label">Nausea</label>
                      <select className="form-select" {...register('careDetails.trimesterSymptoms.nausea')}>
                        <option value="none">None</option>
                        <option value="mild">Mild</option>
                        <option value="moderate">Moderate</option>
                        <option value="severe">Severe</option>
                      </select>
                    </div>

                    <div className="form-group">
                      <label className="form-label">Cramps</label>
                      <select className="form-select" {...register('careDetails.trimesterSymptoms.cramps')}>
                        <option value="none">None</option>
                        <option value="mild">Mild</option>
                        <option value="moderate">Moderate</option>
                        <option value="severe">Severe</option>
                      </select>
                    </div>

                    <div className="form-group">
                      <label className="form-label">Mood</label>
                      <select className="form-select" {...register('careDetails.trimesterSymptoms.mood')}>
                        <option value="stable">Stable</option>
                        <option value="anxious">Anxious</option>
                        <option value="depressed">Depressed</option>
                        <option value="irritable">Irritable</option>
                        <option value="happy">Happy</option>
                      </select>
                    </div>

                    <div className="form-group">
                      <label className="form-label">Fatigue</label>
                      <select className="form-select" {...register('careDetails.trimesterSymptoms.fatigue')}>
                        <option value="none">None</option>
                        <option value="mild">Mild</option>
                        <option value="moderate">Moderate</option>
                        <option value="severe">Severe</option>
                      </select>
                    </div>
                  </div>
                </div>

                <div className="form-section">
                  <h4>Baby Movement</h4>
                  <div className="form-grid">
                    <div className="form-group">
                      <label className="form-label">Movement Frequency</label>
                      <select className="form-select" {...register('careDetails.babyMovement.frequency')}>
                        <option value="not-applicable">Not Applicable</option>
                        <option value="frequent">Frequent</option>
                        <option value="normal">Normal</option>
                        <option value="reduced">Reduced</option>
                        <option value="none">None</option>
                      </select>
                    </div>
                  </div>
                  <div className="form-group">
                    <label className="form-label">Movement Notes</label>
                    <textarea
                      className="form-textarea"
                      rows="2"
                      {...register('careDetails.babyMovement.notes')}
                    />
                  </div>
                </div>

                <div className="form-section">
                  <h4>Sleep & Nutrition</h4>
                  <div className="form-grid">
                    <div className="form-group">
                      <label className="form-label">Sleep Hours</label>
                      <input
                        type="number"
                        min="0"
                        max="24"
                        className="form-input"
                        {...register('careDetails.sleepNutrition.sleepHours')}
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label">Sleep Quality</label>
                      <select className="form-select" {...register('careDetails.sleepNutrition.sleepQuality')}>
                        <option value="excellent">Excellent</option>
                        <option value="good">Good</option>
                        <option value="fair">Fair</option>
                        <option value="poor">Poor</option>
                      </select>
                    </div>

                    <div className="form-group">
                      <label className="form-label">Water Intake (Liters)</label>
                      <input
                        type="number"
                        step="0.1"
                        min="0"
                        className="form-input"
                        {...register('careDetails.sleepNutrition.waterIntake')}
                      />
                    </div>
                  </div>

                  <div className="form-group">
                    <label className="form-label">Diet Notes</label>
                    <textarea
                      className="form-textarea"
                      rows="2"
                      {...register('careDetails.sleepNutrition.dietNotes')}
                    />
                  </div>

                  <div className="supplements-section">
                    <h5>Supplements</h5>
                    {supplementFields.map((field, index) => (
                      <div key={field.id} className="supplement-item">
                        <div className="form-grid">
                          <div className="form-group">
                            <label className="form-label">Supplement Name</label>
                            <input
                              type="text"
                              className="form-input"
                              {...register(`careDetails.sleepNutrition.supplements.${index}.name`)}
                            />
                          </div>
                          <div className="form-group">
                            <label className="form-label">Dosage</label>
                            <input
                              type="text"
                              className="form-input"
                              {...register(`careDetails.sleepNutrition.supplements.${index}.dosage`)}
                            />
                          </div>
                          <div className="form-group">
                            <label className="form-label">Frequency</label>
                            <input
                              type="text"
                              className="form-input"
                              {...register(`careDetails.sleepNutrition.supplements.${index}.frequency`)}
                            />
                          </div>
                        </div>
                        <button
                          type="button"
                          onClick={() => removeSupplement(index)}
                          className="btn btn-danger btn-sm"
                        >
                          <Minus size={14} />
                          Remove
                        </button>
                      </div>
                    ))}
                    <button
                      type="button"
                      onClick={() => appendSupplement({ name: '', dosage: '', frequency: '' })}
                      className="btn btn-outline"
                    >
                      <Plus size={16} />
                      Add Supplement
                    </button>
                  </div>
                </div>
              </>
            )}
          </div>
        )}

        {/* Vital Signs Tab (Obstetrics) / Additional Tab (ENT) */}
        {activeTab === 'vitals' && type === 'obstetrics' && (
          <div className="tab-content">
            <div className="form-section">
              <h4>Vital Signs</h4>
              <div className="form-grid">
                <div className="form-group">
                  <label className="form-label">Systolic BP</label>
                  <input
                    type="number"
                    className="form-input"
                    {...register('vitalSigns.bloodPressure.systolic')}
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Diastolic BP</label>
                  <input
                    type="number"
                    className="form-input"
                    {...register('vitalSigns.bloodPressure.diastolic')}
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Weight (kg)</label>
                  <input
                    type="number"
                    step="0.1"
                    className="form-input"
                    {...register('vitalSigns.weight')}
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Temperature (C)</label>
                  <input
                    type="number"
                    step="0.1"
                    className="form-input"
                    {...register('vitalSigns.temperature')}
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Heart Rate (bpm)</label>
                  <input
                    type="number"
                    className="form-input"
                    {...register('vitalSigns.heartRate')}
                  />
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'vitals' && type === 'ent' && (
          <div className="tab-content">
            <div className="form-section">
              <h4>Additional Information</h4>
              <div className="form-group">
                <label className="form-label">Symptoms</label>
                <textarea
                  className="form-textarea"
                  rows="3"
                  placeholder="Describe any additional symptoms..."
                  {...register('symptoms')}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Next Appointment</label>
                <input
                  type="datetime-local"
                  className="form-input"
                  {...register('nextAppointment')}
                />
              </div>
            </div>
          </div>
        )}

        {/* Form Actions */}
        <div className="form-actions">
          <button
            type="button"
            onClick={handleClose}
            className="btn btn-outline"
            disabled={loading}
          >
            <X size={16} />
            Cancel
          </button>
          <button
            type="submit"
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? (
              <>
                <LoadingSpinner size="small" />
                Creating...
              </>
            ) : (
              <>
                <Save size={16} />
                Create Care Plan
              </>
            )}
          </button>
        </div>
      </form>
    </Modal>
  );
};

export default CreateCarePlanModal;
